import './index.scss';

const Comment = ({ end, index, start, text }) => {
  const commentDate = new Date(start);
  const startDate = `${commentDate.getHours()}:${commentDate.getMinutes()}:${commentDate.getSeconds()}`

  return (
    <h4 key={index}>
      {startDate} {text}
    </h4>
  );
};

const CommentsBox = ({ comments }) => {
  return (
    <div className="comments-box">
      {comments.map((comment, index) => (
        <Comment key={index} {...comment} />
      ))}
    </div>
  );
}

export default CommentsBox;
