import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const Controls = () => {
  return (
    <Form inline>
        <Form.Label className="my-1 mr-2" htmlFor="text">Text</Form.Label>
        <Form.Control as="textarea" className="my-1 mr-sm-2" rows={3} id="text" />
        <Button type="submit" className="my-1">Submit</Button>
    </Form>
  );
}

export default Controls;
<Form inline>
  <Form.Group>
    <Form.Label htmlFor="inputPassword6">Password</Form.Label>
    <Form.Control
      type="password"
      className="mx-sm-3"
      id="inputPassword6"
      aria-describedby="passwordHelpInline"
    />
    <Form.Text id="passwordHelpInline" muted>
      Must be 8-20 characters long.
    </Form.Text>
  </Form.Group>
</Form>
