import React, { useState, useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

import { subscribeToComment } from '../../api';

import VideoPlayer from '../../components/VideoPlayer';
import Controls from '../../components/Controls';

import './index.scss';

const VIDEO_URL = `${process.env.PUBLIC_URL}/Big_Buck_Bunny_1080_10s_5MB.mp4`;
const MESSAGES_DELAY = 3000; // Time in miliseconds

const Home = () => {

  const [comments, setComments] = useState([]);

  useEffect(() => {
    subscribeToComment((err, commentCallback) => {
      setData(comments => [commentCallback, ...comments]);
    }
  )}, [comments]);

  return (
    <div className="App">
      <Container>
        <Row>
          <Col><VideoPlayer videoUrl={VIDEO_URL} /></Col>
        </Row>
        <Row>
          <Col><CommentsBox comments={comments} /></Col>
        </Row>
        <Row>
          <Col><Controls limitTime={MESSAGES_DELAY} /></Col>
        </Row>
      </Container>
    </div>
  );
}

export default Home;
