import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import openSocket from 'socket.io-client';
const  socket = openSocket('http://localhost:8000');

const Controls = () => {

  const [text, setText] = useState('');


  const handleInputChange = (event) => {
    setText(event.target.value);
  }

  const handleKeypress = event => {
    event.preventDefault();
    if(event.keyCode === 13 && event.shiftKey == false) {
      handleSubmit(event);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('textToShow data...' + text)
    socket.emit('textToShow', text);

    // {text: '', start: 'timestamp', end: 'timestamp'}
    return false;
  }

  return (
    <Form inline onSubmit={handleSubmit}>
      <Form.Label className="my-1 mr-2" htmlFor="text">Text</Form.Label>
      <Form.Control as="textarea" onChange={handleInputChange} className="my-1 mr-sm-2" rows={3} id="text" value="text" name="text" onKeyDown={handleKeypress} />
      <Button type="submit" className="my-1">Submit</Button>
    </Form>
  );
}

export default Controls;
